// Проверить, что слово является палиндромом.

const isPalindrome = (word) => {
  const cleanWord = word.trim();
  if (cleanWord.length === 0) return true;
  for (let i = 0; i < cleanWord.length / 2; i++) {
    if (cleanWord[i] !== cleanWord[cleanWord.length - 1 - i]) {
      return false;
    }
  }
  return true;
};

// Примеры использования:
console.log(isPalindrome("топот")); // true
console.log(isPalindrome("дед"));   // true
console.log(isPalindrome("топXт")); // false
console.log(isPalindrome("роза"));  // false
